class Maps(object):
    def __init__(self, imagesx, imagesy):

        self.imagesx = imagesx
        self.imagesy = imagesy

        self.ship_spawnarea_x = (-150, 300)
        self.ship_spawnarea_y = (-1000, 300)
        
        self.pacific_spawnarea_x = (-500, 300)
        self.pacific_spawnarea_y = (-1300, 0)
        
        self.barren_spawnarea_x = (-300, 600)
        self.barren_spawnarea_y = (-1000, 200)
        
        self.town_spawnarea_x = (-1300, 850)
        self.town_spawnarea_y = (-1400, 150)
        
    def blit_map(self, map_choice):
        if map_choice == "SHIP":
            self.ship()
        elif map_choice == "PACIFIC":
            self.pacific()
        elif map_choice == "BARREN":
            self.barren()
        elif map_choice == "TOWN":
            self.town()
        
    def spawn_area(self, map_choice):
        if map_choice == "SHIP":
            self.spawnX, self.spawnY = self.ship_spawnarea_x, self.ship_spawnarea_y
        elif map_choice == "PACIFIC":
            self.spawnX, self.spawnY = self.pacific_spawnarea_x, self.pacific_spawnarea_y
        elif map_choice == "BARREN":
            self.spawnX, self.spawnY = self.barren_spawnarea_x, self.barren_spawnarea_y 
        elif map_choice == "TOWN":
            self.spawnX, self.spawnY = self.town_spawnarea_x, self.town_spawnarea_y
        
    def background_color(self, map_choice): 
        if map_choice == "SHIP":
            return (0,30,240)
        elif map_choice == "PACIFIC":
            return (0,80,240)
        elif map_choice == "BARREN":
            return (139,69,19)
        elif map_choice == "TOWN":
            return (100,100,100)
       
    
